SELECT m1.id_userSend AS id_userSendM1, m1.id_userMatch AS id_userMatchM1, m2.id_userSend AS id_userSendM2, m2.id_userMatch AS id_userMatchM2 FROM match_request AS m1 LEFT JOIN match_request AS m2 ON m1.id_userSend = m2.id_userMatch   WHERE m1.requestState = 1 AND m2.requestState = 1 AND m1.id_userSend = m2.id_userMatch AND m1.id_userMatch = m2.id_userSend AND m1.id_userSend = 1 OR m2.id_userMatch = 1